import {Component} from '@angular/core';
import {NavController, Platform} from 'ionic-angular';
import {Media, MediaObject} from '@ionic-native/media';
import {File} from '@ionic-native/file';
import {InAppBrowser} from "@ionic-native/in-app-browser";
import {DropboxProvider} from '../../providers/dropbox/dropbox';
import {StreamingAudioOptions, StreamingMedia} from "@ionic-native/streaming-media";

@Component({
  selector: 'page-home',
  templateUrl: 'home.html'
})

export class HomePage {
  // @ViewChild('myTrack') myAudio: any;
  isAuth: boolean = false;
  log = [{time: this.getFormattedTime(), msg: 'App initialized'}];
  recording: boolean = false;
  playing: boolean = false;
  filePath: string;
  fileName: string;
  audio: MediaObject;
  audioList: any[] = [];
  sharedLinks: any[] = [];

  constructor(public navCtrl: NavController, private file: File, private media: Media, public platform: Platform,
              private streamingMedia: StreamingMedia, public iab: InAppBrowser, public dropbox: DropboxProvider) {
  }

  getFormattedTime() {
    let time = new Date(),
      year = time.getFullYear(),
      month = time.getMonth()+1,
      day = time.getDate(),
      hours  = time.getHours(),
      minutes = time.getMinutes(),
      seconds = time.getSeconds();

    return year + '-' + month + '-' + day + ' ' + hours + ':' + minutes + ':' + seconds;
  }

  getAudioList() {
    if (localStorage.getItem('audio_list')) {
      this.audioList = JSON.parse(localStorage.getItem('audio_list'));
      console.log('audioList:',this.audioList);
      //this.log.push({time: this.getFormattedTime(), msg: 'Loaded audio list: '+this.audioList.length});
    }
  }

  ionViewDidLoad() {
    //this.accessToken = "";
  }

  ionViewWillEnter() {
    this.isAuth = !!localStorage.getItem('access_token');

    this.getAudioList();
  }

  getFilesPath(replace: boolean) {
    let filesPath: string;

    if (replace) {
      if (this.platform.is('ios')) {
        filesPath = this.file.documentsDirectory.replace(/file:\/\//g, '');
      } else if (this.platform.is('android')) {
        filesPath = this.file.externalDataDirectory.replace(/file:\/\//g, '');
      }
    } else {
      if (this.platform.is('ios')) {
        filesPath = this.file.documentsDirectory;
      } else if (this.platform.is('android')) {
        filesPath = this.file.externalDataDirectory;
      }
    }

    return filesPath;
  }

  startRecord() {
    let date = new Date();
    this.fileName = 'rec-'+date.getDate()+(date.getMonth()+1)+date.getFullYear()+date.getHours()+date.getMinutes()+date.getSeconds()+'.m4a';
    this.filePath = this.getFilesPath(true) + this.fileName;
    //this.log.push({time: this.getFormattedTime(), msg: 'Started record audio: '+this.fileName});

    this.audio = this.media.create(this.filePath);
    this.audio.startRecord();
    this.recording = true;
  }

  stopRecord() {
    this.audio.stopRecord();
    //this.log.push({time: this.getFormattedTime(), msg: 'Stop record audio: '+this.fileName});

    this.audioList.push({filename: this.fileName});
    localStorage.setItem('audio_list', JSON.stringify(this.audioList));
    this.recording = false;
    //this.log.push({time: this.getFormattedTime(), msg: 'Stored audio: '+this.fileName});

    if (this.isAuth)
      this.uploadFile(this.fileName);

    this.getAudioList();
  }

  playAudio(file) {
    this.filePath = this.getFilesPath(true) + file;
    this.audio = this.media.create(this.filePath);
    this.playing = true;
    //this.log.push({time: this.getFormattedTime(), msg: 'Played audio: '+file});

    this.audio.onStatusUpdate.subscribe(status => {
      //this.log.push({time: this.getFormattedTime(), msg: 'onStatusUpdate='+status});
      //console.log('PLAYING AUDIO STATUS: '+status);

      if (status.toString() == '1' || status.toString() == '2') { //player start
        this.playing = true;
      }
      if (status.toString() == '4') { // player end running
        this.playing = false;
      }
    });

    this.audio.onSuccess.subscribe(() => {
      this.playing = false;
      //this.log.push({time: this.getFormattedTime(), msg: 'onSuccess complete'});
    });

    this.audio.onError.subscribe(error => {
      console.error(error);
      //this.log.push({time: this.getFormattedTime(), msg: 'onError='+JSON.stringify(error)});
    });

    this.audio.play();
    this.audio.setVolume(0.8);
  }

  stopAudio() {
    this.audio.stop();
    this.playing = false;
  }

  openUrl(url) {
    const browser = this.iab.create(url);
    browser.show();
  }

  removeAudio(fileName: string) {
    // Remove saved local file
    this.file.removeFile(this.getFilesPath(false), fileName).then(res => {
      console.log('Removed local file:',fileName,res);
      //this.log.push({time: this.getFormattedTime(), msg: 'Removed local file: '+link['name']});

      // Delete from audio array
      for (let i = 0; i < this.audioList.length; i++) {
        if (this.audioList[i].filename === fileName) {
          this.audioList.splice(i, 1);
          localStorage.setItem('audio_list', JSON.stringify(this.audioList));
          break;
        }
      }
    });
  }

  updateAudioLink(link) {
    link['url'] = link['url'].replace('?dl=0','');
    link['url_play'] = link['url'].replace('https://www.','https://dl.');
    this.sharedLinks.push({url: link['url'], url_play: link['url_play'], name: link['name']});
    //this.log.push({time: this.getFormattedTime(), msg: 'Stored to Dropbox: '+link['url']});

    // Remove saved local file
    this.removeAudio(link['name']);
  }

  uploadFile(fileName: string) {
    let filesPath: string = this.getFilesPath(false);
    //this.log.push({time: this.getFormattedTime(), msg: 'Read filesPath: ' + filesPath});

    this.file.readAsArrayBuffer(filesPath, fileName).then(
      data => {
        //console.log('file in 64: ', data);
        //this.log.push({time: this.getFormattedTime(), msg: 'Storing to Dropbox file: ' + filesPath});
        console.log('upload filesPath:',filesPath);

        this.dropbox.uploadFile(fileName, data)
          .subscribe(response => {
            //this.log.push({time: this.getFormattedTime(), msg: 'File stored to Dropbox'});
            console.log('uploadFile response:',response);

            this.dropbox.getSharedLinks(response['id'])
              .subscribe(res => {
                console.log('getSharedLinks res:',res);
                if (res['links'].length) {
                  let link = res['links'][0];

                  this.updateAudioLink(link);
                } else
                  this.dropbox.createSharedLink(response['name'])
                    .subscribe(res => {
                      console.log('createSharedLink res:',res);

                      this.updateAudioLink(res);
                      //resolve(res);
                    }, (err) => {
                      console.log('ERROR createSharedLink:',err);
                      //reject(err);
                    });
              }, (err) => {
                console.log('ERROR getSharedLinks:',err);
                //reject(err);
              });
          }, (err) => {
            console.log('ERROR uploadFile:',err);
            //reject(err);
          });
      }).catch(err => {
        console.log(err);
      });
  }

  playStreamAudio(file) {
    console.log('Played audio: '+file);
    let options: StreamingAudioOptions = {
      bgColor: "#ffffff",
      bgImage: "data:image/png;base64,",
      bgImageScale: "fit", // other valid values: "stretch", "aspectStretch"
      initFullscreen: false, // true is default. iOS only.
      //keepAwake: false, // prevents device from sleeping. true is default. Android only.
      successCallback: function() {
        console.log("Player closed without error.");
      },
      errorCallback: function(errMsg) {
        console.log("Play Audio Error! " + errMsg);
      }
    };

    this.streamingMedia.playAudio(file, options);
  }
}
